import { Observable } from './observable';
import { ease } from './easing';
import { raf } from './raf';

const animations = [];
let ticking;

/**
 * Simple but efficient animation helper with batched requestAnimationFrame
 */
export class Animation extends Observable {
  /**
   * Create new Animation
   * @param  {Number}     [delay=0]     Start delay in milliseconds
   * @param  {Number}     [duration=0]  Duration in milliseconds
   * @param  {String}     [easing='quadOut']      Possible values: 'linear', 'quadIn', 'quadOut', 'quadInOut', 'cubicIn', 'cubicOut', 'cubicInOut', 'quartIn', 'quartOut', 'quartInOut', 'quintIn', 'quintOut', 'quintInOut', 'bounceIn', 'bounceOut', 'bounceInOut'
   * @param  {Function}   [init]          'init' event handler
   * @param  {Function}   [start]         'start' event handler
   * @param  {Function}   [progress]      'progress' event handler
   * @param  {Function}   [end]           'end' event handler
   * @return {Animation}
   */
  constructor ({ delay = 0, duration = 0, easing = ease.quadOut, init, start, progress, end }) {
    super();

    const now = Date.now();

    /**
     * Calculate when to start the animation
     * @type {Number}
     */
    this.startTime = now + delay;
    /**
     * Calculate when to end the animation
     * @type {Number}
     */
    this.endTime = this.startTime + duration;
    /**
     * Which easing to use
     * @type {String}
     */
    this.easing = easing;
    /**
     * Is animation started?
     * @type {Boolean}
     */
    this.started = false;

    if (init) this.on('init', init);
    if (start) this.on('start', start);
    if (progress) this.on('progress', progress);
    if (end) this.on('end', end);

    // add animation
    animations.push(this);

    this.trigger('init');

    if (!ticking) {
      // start ticking
      ticking = true;
      raf(tick);
    }
  }
  /**
   * Destroy the animation
   */
  destroy () {
    for (let i = 0; i < animations.length; i++) {
      if (animations[i] === this) {
        animations.splice(i, 1);
        return;
      }
    }
  }
}

function tick () {
  const now = Date.now();

  if (!animations.length) {
    // stop ticking
    ticking = false;
    return;
  }

  for (let i = 0; i < animations.length; i++) {
    const animation = animations[i];

    if (now < animation.startTime) {
      // animation not yet started..
      continue;
    }
    if (!animation.started) {
      // animation starts
      animation.started = true;
      animation.trigger('start');
    }
    // animation progress
    let t = (now - animation.startTime) / (animation.endTime - animation.startTime);
    if (t > 1) {
      t = 1;
    }
    animation.trigger('progress', animation.easing(t), t);
    if (now > animation.endTime) {
      // animation ended
      animation.trigger('end');
      animations.splice(i--, 1);
      continue;
    }
  }
  raf(tick, true);
}

/**
 * Basic easings
 * @type {Object}
 */
export const ease = { linear, quadIn, quadOut, quadInOut, cubicIn, cubicOut, cubicInOut, quartIn, quartOut, quartInOut, quintIn, quintOut, quintInOut, bounceIn, bounceOut, bounceInOut };

function linear (t) {
  return t;
}

function quadIn (t) {
  return Math.pow(t, 2);
}

function quadOut (t) {
  return 1 - quadIn(1 - t);
}

function quadInOut (t) {
  if (t < 0.5) {
    return quadIn(t * 2) / 2;
  }
  return 1 - quadIn((1 - t) * 2) / 2;
}

function cubicIn (t) {
  return Math.pow(t, 3);
}

function cubicOut (t) {
  return 1 - cubicIn(1 - t);
}

function cubicInOut (t) {
  if (t < 0.5) {
    return cubicIn(t * 2) / 2;
  }
  return 1 - cubicIn((1 - t) * 2) / 2;
}

function quartIn (t) {
  return Math.pow(t, 4);
}

function quartOut (t) {
  return 1 - quartIn(1 - t);
}

function quartInOut (t) {
  if (t < 0.5) {
    return quartIn(t * 2) / 2;
  }
  return 1 - quartIn((1 - t) * 2) / 2;
}

function quintIn (t) {
  return Math.pow(t, 5);
}

function quintOut (t) {
  return 1 - quintOut(1 - t);
}

function quintInOut (t) {
  if (t < 0.5) {
    return quintIn(t * 2) / 2;
  }
  return 1 - quintIn((1 - t) * 2) / 2;
}

function bounceOut (t) {
  const s = 7.5625;
  const p = 2.75;

  if (t < 1 / p) {
    return s * t * t;
  }
  if (t < 2 / p) {
    t -= 1.5 / p;
    return s * t * t + 0.75;
  }
  if (t < 2.5 / p) {
    t -= 2.25 / p;
    return s * t * t + 0.9375;
  }
  t -= 2.625 / p;
  return s * t * t + 0.984375;
}

function bounceIn (t) {
  return 1 - bounceOut(1 - t);
}

function bounceInOut (t) {
  if (t < 0.5) {
    return bounceIn(t * 2) / 2;
  }
  return 1 - bounceIn((1 - t) * 2) / 2;
}

import { eachCSS } from './utils';
import { prefix } from './prefix';

/**
 * HTMLElement helper
 * @param  {String} [tagName='div'] HTMLElement tag name
 * @param  {Object} [attributes={}]   attributes/text/HTML
 * @return {HTMLElement}         Returns pure HTMLElement
 */
export function el (tagName = 'div', attributes = {}) {
  const _el = document.createElement(tagName);

  for (const key in attributes) {
    if (key === 'text') {
      _el.textContent = attributes[key];
    } else if (key === 'html') {
      _el.innerHTML = attributes[key];
    } else if (key === 'style') {
      eachCSS(attributes.style, (propertyName, value) => {
        const prefixedProperty = prefix(propertyName);

        _el.style[prefixedProperty] = value;
      });
    } else {
      _el.setAttribute(key, attributes[key]);
    }
  }
  return _el;
}

export { ease } from './easing';
export { el } from './el';
export { prefix } from './prefix';
export { View } from './view';
export { ViewList } from './viewlist';
export { Animation } from './animation';
export { Observable } from './observable';
export { each, extendable, shuffle } from './utils';
export { translate } from './translate';
export { raf } from './raf';

/**
 * Simple EventEmitter / Observable
 */
export class Observable {
  /**
   * Inits listeners
   * @return {Observable}
   */
  constructor () {
    /**
     * Listeners cache
     * @type {Object}
     */
    this.listeners = {};
  }
  /**
   * Add listener by name
   * @param  {String}     eventName   Event name
   * @param  {Function}   handler     Event handler
   * @return {Observable}
   */
  on (eventName, handler) {
    if (typeof this.listeners[eventName] === 'undefined') {
      this.listeners[eventName] = [];
    }

    this.listeners[eventName].push({ handler, one: false });

    return this;
  }
  /**
   * Add listener by name, which triggers only one
   * @param  {String}     eventName   Event name
   * @param  {Function}   handler     Event handler
   * @return {Observable}
   */
  one (eventName, handler) {
    if (!this.listeners[eventName]) this.listeners[eventName] = [];

    this.listeners[eventName].push({ handler, one: true });

    return this;
  }
  /**
   * Triggers listeners by name
   * @param  {String}     eventName   Event name
   * @param  {...*}          [args]   Call arguments
   * @return {Observable}
   */
  trigger (eventName, ...args) {
    const listeners = this.listeners[eventName];

    if (!listeners) {
      return this;
    }

    for (let i = 0; i < listeners.length; i++) {
      listeners[i].handler.apply(this, args);

      if (listeners[i].one) {
        listeners.splice(i--, 1);
      }
    }

    return this;
  }
  /**
   * Remove all listeners, or by name, or by name & handler
   * @param  {String}     [name]      Event name
   * @param  {Function}   [handler]   Event handler
   * @return {Observable}
   */
  off (name, handler) {
    if (typeof name === 'undefined') {
      this.listeners = {};
    } else if (typeof handler === 'undefined') {
      this.listeners[name] = [];
    } else {
      const listeners = this.listeners[name];

      if (!listeners) {
        return this;
      }

      for (let i = 0; i < listeners.length; i++) {
        if (listeners[i].handler === handler) {
          listeners.splice(i--, 1);
        }
      }
    }

    return this;
  }
}

const style = document.createElement('p').style;
const prefixes = ['webkit', 'moz', 'Moz', 'ms', 'o'];
const memoized = {};

/**
 * Prefixes style property
 * @param  {String} propertyName Style property name to prefix
 * @return {String} Returns prefixed property name
 */
export function prefix (propertyName) {
  if (typeof memoized[propertyName] !== 'undefined') {
    return memoized[propertyName];
  }

  if (typeof style[propertyName] !== 'undefined') {
    memoized[propertyName] = propertyName;
    return propertyName;
  }

  const camelCase = propertyName[0].toUpperCase() + propertyName.slice(1);

  for (let i = 0, len = prefixes.length; i < len; i++) {
    const test = prefixes[i] + camelCase;

    if (typeof style[test] !== 'undefined') {
      memoized[propertyName] = test;

      return test;
    }
  }
}

const hasRequestAnimationFrame = typeof window.requestAnimationFrame !== 'undefined';

/**
 * Simple requestAnimationFrame polyfill
 * @param  {Function} callback Callback
 * @return {Function} requestAnimationFrame or setTimeout -fallback
 */
export function raf (callback) {
  if (hasRequestAnimationFrame) {
    return window.requestAnimationFrame(callback);
  } else {
    return setTimeout(callback, 1000 / 60);
  }
}

raf.cancel = function cancel (id) {
  if (hasRequestAnimationFrame) {
    window.cancelAnimationFrame(id);
  } else {
    clearTimeout(id);
  }
};

import { prefix } from './prefix';
import { isDefined } from './utils';

let has3d;

/**
 * Create translate(x, y) (desktop), translate3d(x, y, 0) (mobile) or translate3d(x, y, z)
 * @param  {Number} [x=0]   Z-coordinate
 * @param  {Number} [y=0]   Z-coordinate
 * @param  {Number} [z=0]   Z-coordinate
 * @return {String}         CSS string
 */
export function translate (x = 0, y = 0, z = 0) {
  if (typeof has3d === 'undefined') {
    has3d = check3d();
  }

  if (has3d || z) {
    return 'translate3d(' + x + ', ' + y + ', ' + z + ')';
  } else {
    return 'translate(' + x + ', ' + y + ')';
  }
}

function check3d () {
  const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);

  if (!isMobile) {
    return false;
  }

  const transform = prefix('transform');
  const $p = document.createElement('p');

  document.body.appendChild($p);
  $p.style[transform] = 'translate3d(1px,1px,1px)';
  has3d = $p.style[transform];

  if (isDefined(has3d) && has3d.length && has3d !== 'none') {
    has3d = true;
  } else {
    has3d = false;
  }

  document.body.removeChild($p);

  return has3d;
}

/**
 * Faster way to iterate array
 * @param  {Array} array    source array
 * @param  {Function} iterator gets called: iterator(array[i], i)
 */
export function each (array, iterator) {
  for (let i = 0; i < array.length; i++) {
    iterator(array[i], i);
  }
}

/**
 * Defined check helper
 * @param  {*}  check Something to check
 * @return {Boolean}       True / false
 */
export function isDefined (check) {
  return typeof check !== 'undefined' || check !== null;
}

/**
 * CSS style string iteration helper
 * @param  {String} styleString     CSS style string
 * @param  {Function} handler Handler function
 */
export function eachCSS (styleString, handler) {
  const styles = styleString.split(';');

  for (let i = 0; i < styles.length; i++) {
    if (!styles[i].length) {
      continue;
    }
    const style = styles[i].split(':');
    const propertyName = style[0].trim();
    const value = style[1].trim();

    if (propertyName.length) {
      handler(propertyName, value);
    }
  }
}

/**
 * Fisher-Yates shuffle helper
 * @param  {Array} array Array to be shuffled
 * @return {Array}       Shuffled Array
 */
export function shuffle (array) {
  if (!array || !array.length) {
    return array;
  }

  for (let i = array.length - 1; i > 0; i--) {
    const rnd = Math.random() * i | 0;
    const temp = array[i];

    array[i] = array[rnd];
    array[rnd] = temp;
  }

  return array;
}
/**
 * Makes Class extendable by adding Class.extend
 * @param  {Class} Class source Class
 * @return {ExtendedClass}       resulted ExtendedClass
 */
export function extendable (Class) {
  Class.extend = function extend (options) {
    return class ExtendedClass extends Class {
      constructor (...args) {
        super(options, ...args);
      }
    };
  };
}

import { extendable } from './utils';
import { prefix } from './prefix';
import { Observable } from './observable';

const EVENT = 'init inited mount mounted unmount unmounted sort sorted update updated destroy'.split(' ').reduce((obj, name) => {
  obj[name] = name;
  return obj;
}, {});

/**
 * VanillaJS helper for single DOM element
 */
export class View extends Observable {
  /**
   * @external {HTMLElement} https://developer.mozilla.org/en-US/docs/Web/API/HTMLElement
   */

  /**
   * @typedef {Object} ViewOptions
   * @property {el|HTMLElement} [el=el('div')] DOM element
   * @property {Function} [init] 'init' event handler shortcut
   * @property {Function} [inited] 'inited' event handler shortcut
   * @property {Function} [mount] 'mount' event handler shortcut
   * @property {Function} [mounted] 'mounted' event handler shortcut
   * @property {Function} [sort] 'sort' event handler shortcut
   * @property {Function} [sorted] 'sorted' event handler shortcut
   * @property {Function} [update] 'update' event handler shortcut
   * @property {Function} [updated] 'updated' event handler shortcut
   * @property {Function} [destroy] 'destroy' event handler shortcut
   * @property {*} [*] Anything else you want to pass on to View
   */

  /**
   * Creates View
   * @param  {ViewOptions} [options] View options
   * @param  {*} [data]    Any data to pass on to init()
   * @return {View}
   */

  constructor (options = {}, data) {
    super();

    /**
     * el attributes cache
     * @type {Object}
     */
    this.attrs = {};
    /**
     * el classNames cache
     * @type {Object}
     */
    this.classes = {};
    /**
     * HTMLElement
     * @type {el|HTMLElement}
     */
    this.el = null;
    /**
     * Proxy event listeners cache
     * @type {Array}
     */
    this.eventListeners = [];
    /**
     * el innerHTML cache
     * @type {String}
     */
    this.html = '';
    /**
     * Listeners cache
     * @type {Object}
     */
    this.listeners = {};
    /**
     * el styles cache
     * @type {Object}
     */
    this.styles = {};
    /**
     * el textContent cache
     * @type {String}
     */
    this.text = '';

    for (const key in options) {
      if (EVENT[key]) {
        this.on(key, options[key]);
      } else {
        this[key] = options[key];
      }
    }

    this.trigger(EVENT.init, data);
    if (!this.el) this.el = document.createElement('div');
    this.el.view = this;
    this.trigger(EVENT.inited, data);
  }
  /**
   * Sets/removes View element attribute (only if changed)
   * @param {String} attributeName   Attribute name
   * @param {*|null} value Attribute value or null to remove
   * @return {View}
   */
  setAttr (attributeName, value) {
    if (!this.attrs) this.attrs = {};

    if (this.attrs[attributeName] === value) {
      return this;
    }
    if (value || value === '') {
      this.el.setAttribute(attributeName, value);
      this.attrs[attributeName] = value;
    } else {
      this.el.removeAttribute(attributeName);
      this.attrs[attributeName] = null;
    }

    return this;
  }
  /**
   * Sets/removes View element class (only if changed)
   * @param {String} className   Class name
   * @param {Boolean} value true / false
   * @return {View}
   */
  setClass (className, value) {
    if (!this.classes) this.classes = {};

    if (this.classes[className] === value) {
      return this;
    }
    if (value) {
      this.el.classList.add(className);
    } else {
      this.el.classList.remove(className);
    }
    this.classes[className] = value;

    return this;
  }
  /**
   * Sets/removes View element style (only if changed)
   * @param {String} propertyName   Style name
   * @param {*|null} value Style value or null to remove
   * @return {View}
   */
  setStyle (propertyName, value) {
    if (!this.styles) this.styles = {};

    if (this.styles[propertyName] === value) {
      return this;
    }

    const prefixed = prefix(propertyName);

    this.el.style[prefixed] = value;
    this.styles[propertyName] = value;

    return this;
  }
  /**
   * Sets View element textContent (only if changed)
   * @param {String} text Text to be applied to textContent
   * @return {View}
   */
  setText (text) {
    if (this.text === text) {
      return this;
    }
    this.el.textContent = text;
    this.text = text;

    return this;
  }
  /**
   * Sets View element innerHTML (only if changed)
   * @param {String} html HTML string
   * @return {View}
   */
  setHTML (html) {
    if (this.html === html) {
      return this;
    }
    this.el.innerHTML = html;
    this.html = html;

    return this;
  }
  /**
   * Adds proxy event listener to View
   * @param {[type]}   listenerName       Listener name
   * @param {Function} handler         Listener handler
   * @param {Boolean}   useCapture Use capture or not
   * @return {View}
   */
  addListener (listenerName, handler, useCapture) {
    const listener = {
      name: listenerName,
      handler,
      proxy: (...args) => {
        handler.apply(this, args);
      }
    };
    if (!this.eventListeners) this.eventListeners = [];

    this.eventListeners.push(listener);
    this.el.addEventListener(name, listener.proxy, useCapture);

    return this;
  }
  /**
   * Removes all proxy event listeners from View, or by name, or by name and handler
   * @param  {String}   [listenerName] Listener name
   * @param  {Function} [handler]   Listener handler
   * @return {View}
   */
  removeListener (listenerName, handler) {
    const listeners = this.eventListeners;
    if (!listeners) {
      return this;
    }
    if (typeof listenerName === 'undefined') {
      for (let i = 0; i < listeners.length; i++) {
        this.el.removeEventListener(listeners[i].proxy);
      }
      this.listeners = [];
    } else if (typeof handler === 'undefined') {
      for (let i = 0; i < listeners.length; i++) {
        if (listeners[i].name === listenerName) {
          listeners.splice(i--, 1);
        }
      }
    } else {
      for (let i = 0; i < listeners.length; i++) {
        const listener = listeners[i];
        if (listener.name === listenerName && handler === listener.handler) {
          listeners.splice(i--, 1);
        }
      }
    }

    return this;
  }
  /**
   * Adds child View/ViewList to View
   * @param {View|ViewList} child Child View/ViewList to be added
   * @return {View}
   */
  addChild (child) {
    if (child.views) {
      child.parent = this;
      return this.setChildren(...child.views);
    }
    let sorting = false;
    if (child.parent) {
      sorting = true;
      child.trigger(EVENT.sort);
    } else {
      child.trigger(EVENT.mount);
    }

    this.el.appendChild(child.el);
    child.parent = this;

    if (sorting) {
      child.trigger(EVENT.sorted);
    } else {
      child.trigger(EVENT.mounted);
    }

    return this;
  }
  /**
   * Adds child View before another View/HTMLElement
   * @param {View} child  Child View to be added
   * @param {View|HTMLElement} before Reference View/HTMLElement
   * @return {View}
   */
  addBefore (child, before) {
    let sorting = false;

    if (child.parent) {
      sorting = true;
      child.trigger(EVENT.sort);
    } else {
      child.trigger(EVENT.mount);
    }

    this.el.insertBefore(child.el, before.el || before);
    child.parent = this;

    if (sorting) {
      child.trigger(EVENT.sorted);
    } else {
      child.trigger(EVENT.mounted);
    }

    return this;
  }
  /**
   * Replace children with Views or ViewList
   * @param {...View|ViewList} views [description]
   * @return {View}
   */
  setChildren (...views) {
    if (views.length && views[0].views) {
      views[0].parent = this;
      if (!views[0].views.length) {
        return this;
      }
      this.setChildren(...views[0].views);
    }
    let traverse = this.el.firstChild;

    for (let i = 0; i < views.length; i++) {
      const view = views[i];

      if (traverse === view.el) {
        traverse = traverse.nextSibling;
        continue;
      }
      if (traverse) {
        this.addBefore(view, traverse);
      } else {
        this.addChild(view);
      }
    }
    while (traverse) {
      const next = traverse.nextSibling;

      if (traverse.view) {
        traverse.view.parent.removeChild(traverse.view);
      } else {
        this.el.removeChild(traverse);
      }

      traverse = next;
    }

    return this;
  }
  /**
   * Remove child View / ViewList
   * @param  {View|ViewList} child Child View/ViewList to be removed
   * @return {View}
   */
  removeChild (child) {
    if (!child.parent) {
      return this;
    }
    child.trigger(EVENT.unmount);

    this.el.removeChild(child.el);
    child.parent = null;

    child.trigger(EVENT.unmounted);

    return this;
  }
  /**
   * Trigger 'update' with data
   * @param  {*} data Any data
   * @return {View}
   */
  update (data) {
    this.trigger(EVENT.update, data);
  }
  /**
   * Destroy View (remove listeners, children, etc..)
   */
  destroy () {
    this.trigger(EVENT.destroy);
    if (this.parent) this.parent.removeChild(this);

    let traverse = this.el.firstChild;

    while (traverse) {
      if (traverse.view) {
        traverse.view.destroy();
      } else {
        this.el.removeChild(traverse);
      }
      traverse = this.el.firstChild;
    }
    this.off();
    this.removeListener();
  }
}

